import React from "react";
import BotCard from "./BotCard";

function BotCollection({ collection, setCollection, removeCollection }) {
  // a function for adding the collection to the your bot army section
  const addToBotArmySection = (bot) => {
    setCollection(
      collection.map((bots) =>
        bots.id === bot.id ? { ...bots, addlist: false } : bots
      )
    );
  };

  return (
    <div className="ui four column grid">
      <div className="row">
        {/*...and here..*/}
        Collection of all bots
        {collection.map((bot) => (
          <BotCard key={bot.id} bot={bot} addlist={addToBotArmySection}

          />
        ))}
      </div>
    </div>
  );
}

export default BotCollection;


