import React, { useState } from "react";
import BotCard from "./BotCard";

function YourBotArmy(yourBot, setYourBot, collection) {
  console.log(yourBot);
  const [botArmy, setBotArmy] = useState(false);

  function toggleArmyStatus(id) {
    setBotArmy(!botArmy);
    setYourBot(
      collection.map((bot) =>
        bot.id === id ? { ...bot, armyBot: botArmy } : bot
      )
    );
  }

  return (
    <div className="ui segment inverted olive bot-army">
      <div className="ui five column grid">
        <div className="row bot-army-row">
          {/*...and here...*/}
          Your Bot Army
          {/* it was givin me map is not a function  */}
          {Array.from(yourBot).map((bot) => (
            (collection.armyBot) ? <BotCard key={bot.id} /> : null
          ))}
        </div>
      </div>
    </div>
  );
}

export default YourBotArmy;
